import UIKit
import Braintree

// Braintree client token is for testing purposes only
//let clientToken = ""

class PaymentsServiceProvider: NSObject {
    let serverURL: String
    var braintreeClient: BTAPIClient?
    
    init(serverURL:String) {
        self.serverURL = serverURL
    }
 
    func getBraintreeClient(completionHandler:(braintreeClient:BTAPIClient?)->Void) {

        print("Create the request")
        // Create the request
        let urlString = "\(serverURL)/payments/braintree_generate_client_token"
        guard let request = URLServiceProvider().getNewGETRequest(withURL: urlString) else { return }
        
        // Execute the request
        print("execute the request")
        let session = NSURLSession.sharedSession()
        let task = session.dataTaskWithRequest(request, completionHandler: {
            
            // Handle the response
            (data:NSData?, response:NSURLResponse?, error:NSError?) -> Void in
            print("Handle the response")
            if error != nil {
                completionHandler(braintreeClient: nil)
                return
            }
            print("\n\nDATA")
            print(data)
            
            print("\n\nRESPONSE")
            print(response)
            
            let clientToken = String(data: data!, encoding: NSUTF8StringEncoding)
            print(clientToken)
            
            let statusCode = (response as? NSHTTPURLResponse)!.statusCode
            switch statusCode {
            case 200: // Catching status code 200, success
                completionHandler(braintreeClient: nil)
            default:
                completionHandler(braintreeClient: nil)
            }
        })
        task.resume()
        
    }
}
